import styled from "styled-components";

export const InputContainer = styled.div`
  width: 100%;
  height: 75px;
  background-color: #BEB5DB;

  display: flex;
  align-items: center;
  justify-content: flex-end;
  font-size: 24px;
  font-family: "Roboto";

  input {
    width: 100%;
    height: 75px;
    background-color: #BEB5DB;
    border: 0;

    padding: 0 10px;
    font-size: 35px;
    color: black;
  }
`;
